import * as types from '../actions';


let initialState = [];
export default function(state = initialState, action) {
  // const user = action.response;
  const user = {
    response: action.response,
    loggedIn: true
  };

  switch(action.type) {
    case types.LOGIN_USER_SUCCESS:
      return { ...state, user };
    case types.LOGIN_USER_ERROR:
      return { ...state, user };
    default:
      return state;
  }
};
