import React, { Component } from 'react';
import {ButtonToolbar, DropdownButton, Dropdown, Form, Col, Button} from 'react-bootstrap';
import {withRouter} from 'react-router'

import {connect} from "react-redux";
import { push } from 'connected-react-router'
import {createProperty, createPropertySuccess} from "../actions/propertyActions";



class PropertyForm extends Component {

  // constructor(props) {
  //   super(props)
  //
  // }
  // componentDidUpdate(){
  //
  // }
  //
  // handleOnChangeName=(event)=>
  // {
  //   this.setState({name: event.target.value});
  // };
  // handleOnChangeAddress=(event)=>
  // {
  //   this.setState({address: event.target.value});
  // };
  // handleOnChangeCity=(event)=>
  // {
  //   this.setState({city: event.target.value});
  // };
  // handleOnChangeState=(event)=>
  // {
  //   this.setState({state: event.target.value});
  // };
  // handleOnChangeZip=(event)=>
  // {
  //   this.setState({zip: event.target.value});
  // };
  // handleOnChangeCleaner=(event)=>
  // {
  //   this.setState({cleaner: event.target.value});
  // };
  // handleOnChangeCalendar=(event)=>
  // {
  //   this.setState({calendar: event.target.value});
  // };

  constructor(props) {
    super(props);
    this.state = {
      'email': '',
      'password': '',
      validate: {
        emailState: '',
      },
    }
    this.handleChange = this.handleChange.bind(this);
  }


  handleChange = async (event) => {
    const { target } = event;
    const value = target.type === 'checkbox' ? target.checked : target.value;
    console.log(value)
    const { id } = target;
    await this.setState({
      [ id ]: value,
    });
  };



  onHandleSave = (event) => {

    event.preventDefault();

    let data = {
      name: this.state.name,
      address: this.state.address,
      city: this.state.city,
      state: this.state.state,
      zip: this.state.zip,
      cleaner: this.state.cleaner,
      calendar:this.state.calendar
    };
    console.log(data);

    this.props.dispatch(createProperty(data));

    console.log('form dispatched create new property action')
  };

  render() {
    let redirect = this.props.createMovie.movie.createRedirect;
    if(redirect){
      this.props.history.push('/movies');
      this.props.dispatch(push('/movies'));
      this.props.dispatch(createPropertySuccess());

    }
    return (
        <div>

            <Form.Label><h4>Property Information</h4></Form.Label>
            <Form.Row>
              <Form.Group as={Col} controlId="formGridEmail">
                <Form.Label>Property Name</Form.Label>
                <Form.Control onChange={ (e) => this.handleChange(e)} type="text" id="title" name="title" className="form-control" placeholder="Bonnie Brae"/>
              </Form.Group>
            </Form.Row>
            <br/>

            <Form.Group controlId="Address">
            <Form.Label><h4>Location</h4></Form.Label>
            <br/>
              <Form.Label>Address</Form.Label>
              <Form.Control onChange={ (e) => this.handleChange(e)}  type="text" id="address" placeholder="1817 S Grant St." />
            </Form.Group>
          <Form.Row>
            <Form.Group as={Col} controlId="formGridAddress1">
              <Form.Label>City</Form.Label>
              <Form.Control onChange={ (e) => this.handleChange(e)}  type="text" id="city"  placeholder="Denver" />
            </Form.Group>
            <Form.Group as={Col} controlId="formGridAddress1">
              <Form.Label>State</Form.Label>
              <Form.Control onChange={ (e) => this.handleChange(e)}  type="text" id="state"  placeholder="Denver" />
            </Form.Group>
            <Form.Group as={Col} controlId="formGridAddress1">
              <Form.Label>Zip</Form.Label>
              <Form.Control onChange={ (e) => this.handleChange(e)}  type="text" id="zip"  placeholder="80210" />
            </Form.Group>
          </Form.Row>
          <br />
          <Form.Row>
            <Form.Group controlId="cleanerID">
              <Form.Label><h4>Property Data Info</h4></Form.Label>
            <br/>
            <Form.Label>Cleaner ID</Form.Label>
              <Form.Control onChange={ (e) => this.handleChange(e)} type="text" id="cleaner" name="cleaner" className="form-control" placeholder="required"/>
            </Form.Group>
            <Form.Group  controlId="cleanerID">
              <Form.Label><h4>Property Data Info</h4></Form.Label>
              <br/>
              <Form.Label>Cleaner ID</Form.Label>
              <Form.Control onChange={ (e) => this.handleChange(e)} type="text" id="cleaner" name="cleaner" className="form-control" placeholder="required"/>
            </Form.Group>
          </Form.Row>
          <br />
          <Form.Row>
            <Form.Group as={Col} controlId="cleanerID">
              <Form.Label><h4>Property Data Info</h4></Form.Label>
              <br/>
              <Form.Label>Cleaner ID</Form.Label>
              <Form.Control onChange={ (e) => this.handleChange(e)} type="text" id="cleaner" name="cleaner" className="form-control" placeholder="required"/>
            </Form.Group>
          </Form.Row>
          <br/>

            <Button variant="primary" onClick={ (e) => this.onHandleSave(e)}>
              Save
            </Button>

        </div>);
  }
}

const mapStateToProps = (createMovie) => ({createMovie});

export default connect(mapStateToProps)(PropertyForm);
