import React from 'react';
import  { Collapse, NavItem, NavLink } from 'reactstrap';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import classNames from 'classnames';
import {updateSelectedMovie} from "../../actions/movieActions";
import {replace} from "connected-react-router";


class SubMenu extends React.Component {
    constructor(props) {
        super(props);

        this.toggleNavbar = this.toggleNavbar.bind(this);
        this.state = {
            collapsed: true
        };
    }

    toggleNavbar() {
        this.setState({
            collapsed: !this.state.collapsed,
        });
    }

    handleOnChangeSelectedOption = (e) => {
        console.log('value of e is ' + e);
        this.props.dispatch(updateSelectedMovie(e));
        this.props.dispatch(replace('/properties/#/' + e));

    };

    render() {
        const { icon, title, items } = this.props;
        return (
            <div>
                <NavItem onClick={this.toggleNavbar} className={classNames({'menu-open': !this.state.collapsed})}>
                    <NavLink className="dropdown-toggle" href="#"><FontAwesomeIcon icon={icon} className="mr-2"/>{title}</NavLink>
                </NavItem>
                <Collapse isOpen={!this.state.collapsed} navbar className={classNames('items-menu',{'mb-1': !this.state.collapsed})}>
                    {items.map(item => (
                        <NavItem key={item.name} className="pl-4" >
                            <NavLink onClick={(e) => this.handleOnChangeSelectedOption(e) } >{item.name}</NavLink >
                        </NavItem>
                    ))}
                </Collapse>
            </div>
        );
    }
}

const mapDispatchToProps = (dispatch) => {
    return {
        updateMovie: (e) => dispatch(updateSelectedMovie(e)),
    };
};

const mapStateToProps = (state) => {
    return {

    };
};
export default connect(mapStateToProps, mapDispatchToProps)(SubMenu);
