import React from 'react';
import { Link } from 'react-router-dom';
import { NavLink as RRNavLink } from 'react-router-dom';
import {
    Collapse,
    Navbar,
    NavbarToggler,
    NavbarBrand,
    Nav,
    NavItem,
    NavLink,
    UncontrolledDropdown,
    DropdownToggle,
    DropdownMenu,
    DropdownItem } from 'reactstrap';
import Cookies from 'js-cookie';

export default class MovieHeader extends React.Component {
    constructor(props) {
        super(props);

        this.toggle = this.toggle.bind(this);
        this.state = {
            isOpen: false
        };
    }
    logout(){
        console.log('removed token');
        Cookies.remove('token');

    }
    toggle() {
        this.setState({
            isOpen: !this.state.isOpen
        });
    }
    render() {
        return (
            <div>
                <Navbar color="light" light expand="md">
                    <NavbarBrand href="/">Mac's Movie Site!</NavbarBrand>
                    <NavbarToggler onClick={this.toggle} />
                    <Collapse isOpen={this.state.isOpen} navbar>
                        <Nav className="ml-auto" navbar>
                            <NavItem>
                                <NavLink tag={RRNavLink} exact to="/movies" activeClassName="active">Movie List</NavLink>
                            </NavItem>
                            <NavItem>
                                <NavLink tag={RRNavLink} exact to="/movie/create" activeClassName="active">New Movie</NavLink>
                            </NavItem>

                            <UncontrolledDropdown nav inNavbar>
                                <DropdownToggle nav caret>
                                    Account
                                </DropdownToggle>

                                <DropdownMenu right>
                                    <DropdownItem tag={RRNavLink} exact to="/login" activeClassName="active">
                                        Login
                                    </DropdownItem>
                                    <DropdownItem tag={RRNavLink} exact to="/register" activeClassName="active">
                                        Signup
                                    </DropdownItem>
                                    <DropdownItem tag={RRNavLink} exact to="/#" activeClassName="active" onClick={this.logout}>
                                    Logout
                                </DropdownItem>


                                </DropdownMenu>
                            </UncontrolledDropdown>
                        </Nav>
                    </Collapse>
                </Navbar>
            </div>
        );
    }
}
