import React, { Component } from 'react';
import { Link, Redirect } from 'react-router-dom';
import { connect } from 'react-redux';
import Cookies from 'js-cookie';
import { Button, Form, FormGroup, Label, Input, FormText, Jumbotron, Col, Row} from 'reactstrap';
import { loginUserAction } from '../actions/authenticationActions';


class LoginPage extends Component {
  constructor(props) {
    super(props);
  }
  componentWillMount() {
    if(Cookies.get('token') !== undefined){
      this.props.history.push('/movies');
    }

  }
  componentWillReceiveProps(newProps) {

  }
  shouldComponentUpdate(newProps, newState) {
    return true;
  }
  componentWillUpdate(nextProps, nextState) {

  }
  componentDidUpdate(prevProps, prevState) {
  }
  componentWillUnmount() {
  }
  componentDidMount() {

  }

  onHandleLogin = (event) => {
    event.preventDefault();

    let username = event.target.username.value;
    let password = event.target.password.value;

    const data = {
      username, password
    };

    this.props.dispatch(loginUserAction(data));
  };

  render() {
    let isSuccess, message;

    if (this.props.response.login.hasOwnProperty('response')) {
      isSuccess = this.props.response.login.response.success;
      message = this.props.response.login.response.message;

      if (isSuccess) {
        Cookies.set('token', this.props.response.login.response.token, {expires: 1});
        console.log('Setting token cookie');
        console.log(Cookies.get());
      }
    }

    return (
        <Container className="App">

          {!isSuccess ? <div>{message}</div> : <Redirect to='/movies' />}
          <h2>Sign In</h2>
          <Form className="loginForm" onSubmit={this.onHandleLogin}>
            <Col>
              <FormGroup>
                <Label>Email</Label>
                <Input
                    type="email"
                    name="email"
                    id="exampleEmail"
                    placeholder="myemail@email.com"
                />
              </FormGroup>
            </Col>
            <Col>
              <FormGroup>
                <Label for="examplePassword">Password</Label>
                <Input
                    type="password"
                    name="password"
                    id="examplePassword"
                    placeholder="********"
                />
              </FormGroup>
            </Col>
            <Button>Submit</Button>
          </Form>
          Don't have account? <Link to='register'>Register here</Link>
        </Container>





    );
  }
}

const mapStateToProps = (response) => ({response});

export default connect(mapStateToProps)(LoginPage);
