import React, {Component} from 'react';
import ReactDOM from 'react-dom';
import { Provider } from 'react-redux';
import { BrowserRouter, Router, Route, Switch } from 'react-router-dom' // react-router v4
import { ConnectedRouter } from 'connected-react-router'

import configureStore, { history } from './store/configureStore';

import './index.css';
import 'bootstrap/dist/css/bootstrap.min.css';


import LoginPage from './components/loginPage';
import RegisterPage from './components/registerPage';
import CreateMovieContainer from './container/createMovieContainer';
import MovieHeader from "./components/header";
import MovieOverview from './container/movieOverview';
import MovieForm from './components/propertyForm';
import Content from "./components/content/Content";
import SideBar from './components/sidebar/SideBar';


const store = configureStore();

class App extends Component {

    constructor(props) {
        super(props);
        this.state = {
            isOpen: true
        };

    }

    toggle = () => {
        this.setState({isOpen: !this.state.isOpen});
    };

render(){
    return (



        <div className="App wrapper">
            <SideBar toggle={this.toggle} isOpen={this.state.isOpen}/>
            <Content toggle={this.toggle} isOpen={this.state.isOpen}/>
            <Provider store={store}>
        <ConnectedRouter history={history}> { /* place ConnectedRouter under Provider */ }
            <Router history={history}>
                <Switch >
                    <Route path='/' exact component={MovieOverview} />
                    <Route path='/login' component={LoginPage} />
                    <Route path='/register' component={RegisterPage} />
                    <Route path="/properties" component={MovieOverview} />
                    <Route path='/property/create' exact component={CreateMovieContainer}/>
                    <Route path='/test/:id' exact component={RegisterPage} />
                </Switch>
            </Router>
        </ConnectedRouter>
            </Provider>
        </div>



    );
}
}
export default App;
